mapper.insert:
insert into document_catalog (project_id, parent_id, created_date, updated_date, title, folder)
select #{projectId}, #{parentId}, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, #{title}, #{folder}
from owner_project as p
where p.id = #{projectId}
  and p.contains_docs = false

