mapper.insert:
insert into document (project_id, title, description, repo_url)
select #{projectId}, #{title}, #{description}, #{repoUrl}
from owner_project as p
where p.id = #{projectId}

insert into document_catalog (project_id, document_id, parent_id, created_date, updated_date, title, folder)
select #{projectId}, #{parentUid}, #{documentId}, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, #{title}, #{folder}
from document as d
where d.id = #{documentId}

insert into document_catalog (project_id, parent_id, document_id, created_date, updated_date, title, folder)
select #{projectId}, #{parentUid}, #{documentId}, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, #{title}, #{folder}
from document_catalog as c
where c.id = #{parentUid}
  and c.folder != #{exclude}

mapper.update:
update document_catalog
set folder = #{folder}
where uid = #{uid}
limit 1

mapper.insert:
insert into document_chapter (created_date, updated_date, title, isEmpty, download_link, upload_link,
                              catalog_id)
select CURRENT_TIMESTAMP, CURRENT_TIMESTAMP, #{title}, #{isEmpty}, #{downloadLink}, #{uploadLink}, #{catalogUid}
from document_catalog as c
where c.id = #{catalogUid}
  and c.folder != #{exclude}
